package day12forloopdt;

public class forloop02 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		for (int i=1; i<=20; i++) {
			System.out.println("Mehmet");
		}
		
		//
		for (int i=3; i<21; i++) {
			System.out.print(i +" ");
		}
		
		System.out.println();
		
		for( int i=20; i>2; i--) {
			System.out.print(i +" ");
		}
		System.out.println();
		
			for (int i=12; i<33; i=i+2) { // i+=2
				System.out.print(i+" ");
			}
		
			System.out.println();
			
			for(int i=12; i<=32; i++)
				if(i%2==0) {
					System.out.print(i + " ");					
				}
			System.out.println();
			
			for(int i=12; i<=32; i++) {
				if (i%2==1) {
					System.out.print(i +" ");
				}
			}
				System.out.println();
				
			for (int i=13; i<=32; i=i+2) { //i+=2
					System.out.print(i+" ");
			   }
			
			
			
	}

	}
